import pytest
from longest_pal_substr import Solution

@pytest.mark.parametrize('entrada, esperado', [
    ("tracecars", "racecar"),
    ("a", "a"),
    ("ac", "a"),
    ("aac", "aa"),
    ("babad", "bab"),
    ("cbbd", "bb"),
    ("aacabdkacaa", "aca"),
    ("eabcb", "bcb"),
    ("abbcccbbbcaaccbababcbcabca", "bbcccbb"),
    ("abb", "bb"),
    ("aaaaaaaaaaaaaaaaaaaa", "aaaaaaaaaaaaaaaaaaaa"),
    ("0123456789876543210", "0123456789876543210"),
    ("", "")
])

def test_longestPalindrome(entrada, esperado):
    assert Solution().longestPalindrome(entrada) == esperado